import React, { Component } from "react"
import img from "../images/pricing.jpg"
import Back from "../common/Back"
import "./newmission.css"
import Mission from '../../abis/Mission.json'
import { ethers } from 'ethers';

import ipfs from '../../ipfs';
import { useNavigate } from 'react-router-dom'

class Newmission extends Component {

  constructor(props) {
    super(props);
    this.state = {
      account: '',
      centerCount: 0,    
      loading: true,
      fileUrl: '',
      centers: [],
      name: '',
      image: '',
      address_: '',
      contact: '',
      location: '',
      email: '',
      mission: null,
      account: null,
    };
    this.contractAddress = '0x5FbDB2315678afecb367f032d93F642f64180aa3';
  }

  async componentWillMount() {
       await this.loadBlockchainData()
  }


  async loadBlockchainData() {
    try {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      console.log(provider);
      const network = await provider.getNetwork();
      const mission = new ethers.Contract(this.contractAddress, Mission, provider);

      this.setState({ mission })      
      this.setState({ loading: false })

      window.ethereum.on('accountsChanged', async () => {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        const account = ethers.utils.getAddress(accounts[0]);
        this.setState({ account });
      });
    } catch (err) {
      console.log(err);
      alert('Failed to load blockchain data.');
    }
  }





  handleNameChange = (event) => {
    this.setState({ name: event.target.value });
  };

  handleImageChange = (event) => {
    this.setState({ image: event.target.value });
  };

  handleAddressChange = (event) => {
    this.setState({ address_: event.target.value });
  };

  handleContactChange = (event) => {
    this.setState({ contact: event.target.value });
  };

  handleLocationChange = (event) => {
    this.setState({ location: event.target.value });
  };

  handleEmailChange = (event) => {
    this.setState({ email: event.target.value });
  };

  handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const missionContract = new ethers.Contract(this.contractAddress, Mission, signer);

      await missionContract.createCenter(
        this.state.name,
        this.state.image,
        this.state.address_,
        this.state.contact,
        this.state.location,
        this.state.email
      );

      this.setState({
        name: '',
        image: '',
        address_: '',
        contact: '',
        location: '',
        email: '',
      });

      alert('Center created successfully!');
    } catch (err) {
      console.log(err);
      alert('Failed to create center.');
    }
  };



  render() {
    return (
      <>
        <section className='contact mb'>
          <Back name='Mission registration' title='Join our network of missionaries' cover={img} />
          <p>&nbsp;</p>
          <div className="container">
            <div className="row">
              <div className="col-lg-8">
                <div className="card">
                  <div className="card-header">
                    <h4 className="card-title">Register mission</h4>
                  </div>
                  {this.state.loading
                    ? <div id="loader" className="text-center"><p className="text-center">Loading...</p></div>
                    :
                    <div className="card-body">
                      <form
                        onSubmit={(event) => {
                          event.preventDefault()
                          const center_name = this.centerName.value
                          const featured_image = this.state.fileUrl
                          const center_address = this.centerAddress.value
                          const contact = this.centerContact.value
                          const location = this.centerLocation.value
                          const email = this.centerEmail.value
                          this.createCenter(center_name, featured_image, center_address, contact, location, email)
                        }}
                      >
                        <div className="form-group row">
                          <label className="col-sm-3 col-form-label">Mission name</label>
                          <div className="col-sm-9">
                            <input id="centerName" ref={(input) => { this.centerName = input }} className="form-control form-control-sm" type="text" placeholder="Ex: St John Bosco" />
                          </div>
                        </div>

                        <div className="form-group row">
                          <label className="col-sm-3 col-form-label">Address</label>
                          <div className="col-sm-9">
                            <input id="centerAddress" ref={(input) => { this.centerAddress = input }} className="form-control form-control-sm" type="text" placeholder="Ex: P.O.Box 2222" />
                          </div>
                        </div>

                        <div className="form-group row">
                          <label className="col-sm-3 col-form-label">Location</label>
                          <div className="col-sm-9">
                            <input id="centerLocation" ref={(input) => { this.centerLocation = input }} className="form-control form-control-sm" type="text" placeholder="Ex: Street name & Number" />
                          </div>
                        </div>

                        <div className="form-group row">
                          <label className="col-sm-3 col-form-label">Contact</label>
                          <div className="col-sm-9">
                            <input id="centerContact" ref={(input) => { this.centerContact = input }} className="form-control form-control-sm" type="number" placeholder="Ex: 078....." />
                          </div>
                        </div>

                        <div className="form-group row">
                          <label className="col-sm-3 col-form-label">Email</label>
                          <div className="col-sm-9">
                            <input id="centerEmail" ref={(input) => { this.centerEmail = input }} className="form-control form-control-sm" type="text" placeholder="Ex: mission@globalmission.org" />
                          </div>
                        </div>

                        <div className="form-group row">
                          <label className="col-sm-3 col-form-label">Featured Image</label>
                          <div className="col-sm-9">
                            <input type="file" onChange={this.onChange} accept=".jpg, .png, .svg" name="image" id="image" className="form-control-file" />
                          </div>
                        </div>

                        <div className="form-group row">
                          <div className="col-sm-3">
                          </div>
                          <div className="col-sm-9">
                            <button type="submit" className="btn btn-primary btn-sm">Submit</button>
                          </div>
                        </div>
                      </form>
                    </div>
                  }


                </div>
              </div>

            </div>
          </div>
        </section>
      </>
    );
  }
}

export default Newmission
