import React, { Component } from "react"
import img from "../images/pricing.jpg"
import Back from "../common/Back"
import "./newmission.css"
import Mission from '../../abis/Mission.json'
import Web3 from 'web3'
import ipfs from '../../ipfs';


class Newmission extends Component {

  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3
    // Load account
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    const networkId = await web3.eth.net.getId()
    const networkData = Mission.networks[networkId]
    if (networkData) {
      const mission = web3.eth.Contract(Mission.abi, networkData.address)
      this.setState({ mission })
      const centerCount = await mission.methods.centerCount().call()
      this.setState({ centerCount })
      // Load centers
      for (var i = 1; i <= centerCount; i++) {
        const center = await mission.methods.centers(i).call()
        this.setState({
          centers: [...this.state.centers, center]
        })
      }
      this.setState({ loading: false })
    } else {
      window.alert('Mission contract not deployed to detected network.')
    }
  }

  constructor(props) {
    super(props)
    this.state = {
      account: '',
      centerCount: 0,
      centers: [],
      loading: true,
      fileUrl: ''
    }

    this.createCenter = this.createCenter.bind(this)
  }

  onChange = async (e) => {
    const file = e.target.files[0]
    try {
      const added = await ipfs.add(file)
      const url = `https://global-mission.infura-ipfs.io/ipfs/${added.path}`
      this.setState({ fileUrl: url })
      console.log(url)
    } catch (error) {
      console.log('Error uploading file: ', error)
    }
  };


  createCenter(center_name, featured_image, center_address, contact, location, email) {
    this.setState({ loading: true })
    this.state.mission.methods.createCenter(center_name, featured_image, center_address, contact, location, email
      ).send({ 
        from: this.state.account,
       // gas: 1500000,
        //gasPrice: '30000000000'
       })
      .once('receipt', (receipt) => {
        this.setState({ loading: false })
      })
  }


  render() {
    return (
      <>
        <section className='contact mb'>
          <Back name='Mission registration' title='Join our network of missionaries' cover={img} />
          <p>&nbsp;</p>
          <div className="container">
            <div className="row">
              <div className="col-lg-8">
                <div className="card">
                  <div className="card-header">
                    <h4 className="card-title">Register mission</h4>
                  </div>
                  {this.state.loading
                    ? <div id="loader" className="text-center"><p className="text-center">Loading...</p></div>
                    :
                    <div className="card-body">
                      <form
                        onSubmit={(event) => {
                          event.preventDefault()
                          const center_name = this.centerName.value
                          const featured_image = this.state.fileUrl
                          const center_address = this.centerAddress.value
                          const contact = this.centerContact.value
                          const location = this.centerLocation.value
                          const email = this.centerEmail.value
                          this.createCenter(center_name, featured_image, center_address, contact, location, email)
                        }}
                      >
                        <div className="form-group row">
                          <label className="col-sm-3 col-form-label">Mission name</label>
                          <div className="col-sm-9">
                            <input id="centerName" ref={(input) => { this.centerName = input }} className="form-control form-control-sm" type="text" placeholder="Ex: St John Bosco" />
                          </div>
                        </div>

                        <div className="form-group row">
                          <label className="col-sm-3 col-form-label">Address</label>
                          <div className="col-sm-9">
                            <input id="centerAddress" ref={(input) => { this.centerAddress = input }} className="form-control form-control-sm" type="text" placeholder="Ex: P.O.Box 2222" />
                          </div>
                        </div>

                        <div className="form-group row">
                          <label className="col-sm-3 col-form-label">Location</label>
                          <div className="col-sm-9">
                            <input id="centerLocation" ref={(input) => { this.centerLocation = input }} className="form-control form-control-sm" type="text" placeholder="Ex: Street name & Number" />
                          </div>
                        </div>

                        <div className="form-group row">
                          <label className="col-sm-3 col-form-label">Contact</label>
                          <div className="col-sm-9">
                            <input id="centerContact" ref={(input) => { this.centerContact = input }} className="form-control form-control-sm" type="number" placeholder="Ex: 078....." />
                          </div>
                        </div>

                        <div className="form-group row">
                          <label className="col-sm-3 col-form-label">Email</label>
                          <div className="col-sm-9">
                            <input id="centerEmail" ref={(input) => { this.centerEmail = input }} className="form-control form-control-sm" type="text" placeholder="Ex: mission@globalmission.org" />
                          </div>
                        </div>

                        <div className="form-group row">
                          <label className="col-sm-3 col-form-label">Featured Image</label>
                          <div className="col-sm-9">
                            <input type="file" onChange={this.onChange} accept=".jpg, .png, .svg" name="image" id="image" className="form-control-file" />
                          </div>
                        </div>

                        <div className="form-group row">
                          <div className="col-sm-3">
                          </div>
                          <div className="col-sm-9">
                            <button type="submit" className="btn btn-primary btn-sm">Submit</button>
                          </div>
                        </div>
                      </form>
                    </div>
                  }


                </div>
              </div>

            </div>
          </div>
        </section>
      </>
    );
  }
}

export default Newmission
