import React, { Component } from "react";
import img from "../images/pricing.jpg";
import Back from "../common/Back";
import "./newmission.css";
import { ethers } from "ethers";
import Mission from "../../abis/Mission.json";
import ipfs from "../../ipfs";
import { useNavigate, Link } from "react-router-dom";

class Newmission extends Component {
    constructor(props) {
        super(props);
        this.state = {
            account: "",
            centerCount: 0,
            centers: [],
            loading: true,
            fileUrl: "",
        };
        this.contractAddress = "0x5FbDB2315678afecb367f032d93F642f64180aa3";
    }

    async componentDidMount() {
        await this.loadBlockchainData();
        this.fetchCenters();
    }

    async loadBlockchainData() {
        try {
            const provider = new ethers.providers.Web3Provider(window.ethereum);
            const network = await provider.getNetwork();
            const mission = new ethers.Contract(
                this.contractAddress,
                Mission,
                provider
            )

            this.setState({ mission })           

            this.setState({ loading: false });


            window.ethereum.on("accountsChanged", async () => {
                const accounts = await window.ethereum.request({
                    method: "eth_requestAccounts",
                });
                const account = ethers.utils.getAddress(accounts[0]);
                this.setState({ account });
            });
        } catch (error) {
            console.log(error);
        }
    }

    async fetchCenters() {
        //const centers = [];
        const centerCount = await this.state.mission.getCenterCount();

        for (let i = 0; i < centerCount; i++) {
          const center = await this.state.mission.centers(i);
          this.centers.push(center);
        }

        this.setState({ centers });
      }

    render() {
        return (
            <>
                <section className='contact mb'>
                    <Back name='Registered centers' title='Missionaries network center' cover={img} />
                    <p>&nbsp;</p>
                    <div className="container">
                        <div className="row">
                            <div className="col-lg-12">
                                <div className="card">
                                    <div className="card-header">
                                        <h5 className="card-title">All centers</h5>
                                    </div>
                                    {this.state.loading
                                        ? <div id="loader" className="text-center"><p className="text-center">Loading...</p></div>
                                        :
                                        <div className="card-body">
                                            <table class="table">
                                                <thead>
                                                    <tr>
                                                        <th scope="col">#</th>
                                                        <th scope="col">Owner</th>
                                                        <th scope="col">Name</th>
                                                        <th scope="col">Address</th>
                                                        <th scope="col">Location</th>
                                                        <th scope="col">Contact</th>
                                                        <th scope="col">Email</th>
                                                        <th scope="col">Action</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {this.state.centers.map((center, key) => {
                                                        return (
                                                            <tr key={key}>
                                                                <th scope="row">1</th>
                                                                <td>{center.owner}</td>
                                                                <td>{center.center_name}</td>
                                                                <td>{center.center_address}</td>
                                                                <td>{center.location}</td>
                                                                <td>{center.contact}</td>
                                                                <td>{center.email}</td>
                                                                <td><Link to='' className="btn btn-primary btn-sm"> Approve </Link> </td>
                                                            </tr>
                                                        )
                                                    })}
                                                </tbody>
                                            </table>

                                        </div>
                                    }


                                </div>
                            </div>

                        </div>
                    </div>
                </section>
            </>
        );
    }
}

export default Newmission
