import React, { Component } from "react"
import Back from "../common/Back"
import Heading from "../common/Heading"
import img from "../images/about.jpg"
import "./viewmission.css"
import Mission from '../../abis/Mission.json'
import Accomodation from '../../abis/Accommodation.json'
import Web3 from 'web3'
import { Link } from "react-router-dom"



class Viewmission extends Component {
  async componentWillMount() {
    await this.loadWeb3()
    await this.loadMissionData()
    await this.loadApartmentsData()
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  constructor(props) {
    super(props)
    this.state = {
      center_id: null,
      center_name: '',
      featured_image: '',
      mission_address: '',
      contact: '',
      location: '',
      email: '',
      accitemCount: 0,
      apartments: [],
    }
  }

  async loadMissionData() {
    const web3 = window.web3
    // Load account
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    const networkId = await web3.eth.net.getId()
    const networkData = Mission.networks[networkId]
    if (networkData) {
      const mission = web3.eth.Contract(Mission.abi, networkData.address)
      this.setState({ mission })
      const centerCount = await mission.methods.centerCount().call()
      this.setState({ centerCount })

      // Load center
      for (var i = 1; i <= centerCount; i++) {
        const center = await mission.methods.centers(i).call()
        if (center.owner === this.state.account) {
          this.setState({
            center_id: center.id,
            center_name: center.center_name,
            featured_image: center.featured_image,
            center_address: center.mission_address,
            contact: center.contact,
            location: center.location,
            email: center.email
          });
        } else {
          console.log("No Center found", 9);
        }
      }
      this.setState({ loading: false })
    } else {
      window.alert('Mission contract not deployed to detected network.')
    }
  }

  async loadApartmentsData() {
    const web3 = window.web3
    // Load account
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    const networkId = await web3.eth.net.getId()
    const networkData = Accomodation.networks[networkId]
    if (networkData) {
      const accommodation = web3.eth.Contract(Accomodation.abi, networkData.address)
      this.setState({ accommodation })
      const accitemCount = await accommodation.methods.accitemCount().call()
      this.setState({ accitemCount })

      // Load apartments
      for (var i = 1; i <= accitemCount; i++) {
        const apartment = await accommodation.methods.accitems(i).call()
        if (apartment.owner == this.state.account) {
          this.setState({
            apartments: [...this.state.apartments, apartment]
          })
        } else {
          console.log("This hotel has no rooms");
        }
      }
      this.setState({ loading: false })
    } else {
      window.alert('Mission contract not deployed to detected network.')
    }
  }


  async closeModal() {
    this.setState({ show: false });
  }

  async showModal(event) {
    await this.setState({ roomId: event.target.id });
    this.setState({ show: true });
    console.log("Current Room ID ", this.state.roomId);
  }

  key = async (e) => {
    const file = e.target.files[0]
    try {
      this.setState({ nights: e.target.value })
      console.log('name is: ', this.state.nights)

    } catch (error) {
      console.log('Error: ', error)
    }
  };

  render() {
    return (
      <>
        <section className='My Dashboard'>
          <Back name='About Us' title={`Welcome ${this.state.center_name} Center/Mission`} cover={img} />
          <div className='container flex mtop'>
            <div>
              <p>Address: {this.state.center_address}</p>
              <p>Contact: {this.state.contact}</p>
              <p>Location: {this.state.location}</p>
              <p>Email: {this.state.email}</p>

              <Link to={`/newapartment/${this.state.center_id}`} className='btn btn-primary btn-small'>List Apartment</Link>
            </div>
            <div className='right row'>
              <img src={this.state.featured_image} className="rounded mission-img img-thumbnail float-right" />
            </div>
          </div>
          <br />

          <div className='container flex'>

            <div className="content grid3 mtop">
              {this.state.apartments.map((apartment, key) => {
                return (
                  <div className='box shadow' key={key}>
                    <div className='img'>
                      <Link to={`viewlisting/${apartment.id}`}>
                        <img src={apartment.image} alt='' />
                      </Link>
                    </div>
                    <div className='container text'>
                      <div className='category flex'>
                        <i className='fa fa-heart'></i>
                      </div>
                      <h5>{apartment.name}</h5>
                      <p className="smallfont">
                        <i className='fa fa-location-dot'></i>{apartment.owner}
                      </p>
                      <p>
                        <i className='fa fa-location-dot'></i>Location
                      </p>
                    </div>
                    <div className='container button flex'>
                      <div>
                        <button onClick={this.showModal} className='btn btn-primary btn-sm'>{window.web3.utils.fromWei(apartment.price.toString(), 'Ether')} Eth</button> <label htmlFor=''>/Night</label>
                      </div>
                      <span>Type</span>
                    </div>
                    <br />
                  </div>
                )
              })}
            </div>
          </div>

          <br />

        </section>


      </>
    );
  }
}

export default Viewmission
