import React, { Component } from "react"
import img from "../images/pricing.jpg"
import Back from "../common/Back"
import "./newlisting.css"
import Accomodation from '../../abis/Accommodation.json'
import Web3 from 'web3'
import { useParams } from "react-router-dom";
import ipfs from '../../ipfs';

function withParams(Component) {
    return props => <Component {...props} params={useParams()} />;
}

class Newmission extends Component {

    async componentWillMount() {
        await this.loadWeb3()
        await this.loadBlockchainData()
    }

    async loadWeb3() {
        if (window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
        }
        else if (window.web3) {
            window.web3 = new Web3(window.web3.currentProvider)
        }
        else {
            window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
        }
    }

    async loadBlockchainData() {
        const web3 = window.web3
        // Load account
        const accounts = await web3.eth.getAccounts()
        this.setState({ account: accounts[0] })
        const networkId = await web3.eth.net.getId()
        const networkData = Accomodation.networks[networkId]
        if (networkData) {
            const accommodation = web3.eth.Contract(Accomodation.abi, networkData.address)
            this.setState({ accommodation })
            const accitemCount = await accommodation.methods.accitemCount().call()
            this.setState({ accitemCount })
            // Load apartments
            for (var i = 1; i <= accitemCount; i++) {
                const apartment = await accommodation.methods.accitems(i).call()
                this.setState({
                    apartments: [...this.state.apartments, apartment]
                })
            }
            this.setState({ loading: false })
        } else {
            window.alert('Mission contract not deployed to detected network.')
        }
    }

    constructor(props) {
        super(props)
        this.state = {
            account: '',
            accitemCount: 0,
            apartments: [],
            loading: true,
            fileUrl: ''
        }
        this.createCenter = this.createApartment.bind(this)
    }
    onChange = async (e) => {
        const file = e.target.files[0]
        try {
            const added = await ipfs.add(file)
            const url = `https://global-mission.infura-ipfs.io/ipfs/${added.path}`
            this.setState({ fileUrl: url })
            console.log(url)
        } catch (error) {
            console.log('Error uploading file: ', error)
        }
    };
    createApartment(center_id, name, image, price, room_num, bed_num, description) {
        this.setState({ loading: true })
        this.state.accommodation.methods.createAccitem(center_id, name, image, price, room_num, bed_num, description).send({ from: this.state.account })
            .once('receipt', (receipt) => {
                this.setState({ loading: false })
            })
    }

    render() {
        return (
            <>
                <section className='contact mb'>
                    <Back name='Add Apartment' title='Missionaries network Apartment' cover={img} />
                    <p>&nbsp;</p>
                    <div className="container">
                        <div className="row">
                            <div className="col-lg-8">
                                <div className="card">
                                    <div className="card-header">
                                        <h5 className="card-title">Add Apartments</h5>
                                    </div>
                                    {this.state.loading
                                        ? <div id="loader" className="text-center"><p className="text-center">Loading...</p></div>
                                        :
                                        <div className="card-body">
                                            <form
                                                onSubmit={(event) => {
                                                    event.preventDefault()
                                                    const center_id = this.props.params.id
                                                    const name = this.apartmentName.value
                                                    const image = this.state.fileUrl
                                                    const price = window.web3.utils.toWei(this.apartmentPrice.value.toString(), 'Ether')
                                                    const room_num = this.apartmentRooms.value
                                                    const bed_num = this.apartmentBeds.value
                                                    const description = this.apartmentDescription.value
                                                    this.createCenter(center_id, name, image, price, room_num, bed_num, description)
                                                }}
                                            >
                                                <div className="form-group row">
                                                    <label className="col-sm-3 col-form-label">Name</label>
                                                    <div className="col-sm-9">
                                                        <input id="apartmentName" ref={(input) => { this.apartmentName = input }} className="form-control form-control-sm" type="text" placeholder="Ex: Room 1" />
                                                    </div>
                                                </div>

                                                <div className="form-group row">
                                                    <label className="col-sm-3 col-form-label">Price</label>
                                                    <div className="col-sm-9">
                                                        <input id="apartmentPrice" ref={(input) => { this.apartmentPrice = input }} className="form-control form-control-sm" type="text" placeholder="Ex: 200" />
                                                    </div>
                                                </div>

                                                <div className="form-group row">
                                                    <label className="col-sm-4 col-form-label">Number of rooms</label>
                                                    <div className="col-sm-2">
                                                        <input id="apartmentRooms" ref={(input) => { this.apartmentRooms = input }} className="form-control form-control-sm" type="number" placeholder="Ex: 5" />
                                                    </div>
                                                    <label className="col-sm-4 col-form-label">Number of beds</label>
                                                    <div className="col-sm-2">
                                                        <input id="apartmentBeds" ref={(input) => { this.apartmentBeds = input }} className="form-control form-control-sm" type="number" placeholder="Ex: 12" />
                                                    </div>
                                                </div>

                                                <div className="form-group row">
                                                    <label className="col-sm-3 col-form-label">Desrciption</label>
                                                    <div className="col-sm-9">
                                                        <textarea id="apartmentDescription" ref={(input) => { this.apartmentDescription = input }} className="form-control" rows="3"></textarea>
                                                    </div>
                                                </div>

                                                <div className="form-group row">
                                                    <label className="col-sm-3 col-form-label">Featured Image</label>
                                                    <div className="col-sm-9">
                                                        <input type="file" onChange={this.onChange} accept=".jpg, .png, .svg" name="image" id="image" className="form-control-file" />
                                                    </div>
                                                </div>

                                                <div className="form-group row">
                                                    <div className="col-sm-3">
                                                    </div>
                                                    <div className="col-sm-9">
                                                        <button type="submit" className="btn btn-primary btn-sm">Submit</button>
                                                    </div>
                                                </div>
                                            </form>

                                        </div>
                                    }
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </>
        );
    }
}

export default withParams(Newmission)
