'use strict';

var ip = require('./ip');
var protocols = require('./protocols-table');
var CID = require('cids');
var multibase = require('multibase');
var varint = require('varint');
var uint8ArrayToString = require('uint8arrays/to-string');
var uint8ArrayFromString = require('uint8arrays/from-string');
var uint8ArrayConcat = require('uint8arrays/concat');
module.exports = Convert;

// converts (serializes) addresses
/**
 * @param {string} proto
 * @param {string | Uint8Array} a
 */
function Convert(proto, a) {
  if (a instanceof Uint8Array) {
    return Convert.toString(proto, a);
  } else {
    return Convert.toBytes(proto, a);
  }
}

/**
 * Convert [code,Uint8Array] to string
 *
 * @param {number|string} proto
 * @param {Uint8Array} buf
 * @returns {string}
 */
Convert.toString = function convertToString(proto, buf) {
  var protocol = protocols(proto);
  switch (protocol.code) {
    case 4: // ipv4
    case 41:
      // ipv6
      return bytes2ip(buf);
    case 6: // tcp
    case 273: // udp
    case 33: // dccp
    case 132:
      // sctp
      return bytes2port(buf).toString();
    case 53: // dns
    case 54: // dns4
    case 55: // dns6
    case 56: // dnsaddr
    case 400: // unix
    case 777:
      // memory
      return bytes2str(buf);
    case 421:
      // ipfs
      return bytes2mh(buf);
    case 444:
      // onion
      return bytes2onion(buf);
    case 445:
      // onion3
      return bytes2onion(buf);
    default:
      return uint8ArrayToString(buf, 'base16');
    // no clue. convert to hex
  }
};

Convert.toBytes = function convertToBytes( /** @type {string | number } */proto, /** @type {string} */str) {
  var protocol = protocols(proto);
  switch (protocol.code) {
    case 4:
      // ipv4
      return ip2bytes(str);
    case 41:
      // ipv6
      return ip2bytes(str);
    case 6: // tcp
    case 273: // udp
    case 33: // dccp
    case 132:
      // sctp
      return port2bytes(parseInt(str, 10));
    case 53: // dns
    case 54: // dns4
    case 55: // dns6
    case 56: // dnsaddr
    case 400: // unix
    case 777:
      // memory
      return str2bytes(str);
    case 421:
      // ipfs
      return mh2bytes(str);
    case 444:
      // onion
      return onion2bytes(str);
    case 445:
      // onion3
      return onion32bytes(str);
    default:
      return uint8ArrayFromString(str, 'base16');
    // no clue. convert from hex
  }
};

/**
 * @param {string} ipString
 */
function ip2bytes(ipString) {
  if (!ip.isIP(ipString)) {
    throw new Error('invalid ip address');
  }
  return ip.toBytes(ipString);
}

/**
 * @param {Uint8Array} ipBuff
 */
function bytes2ip(ipBuff) {
  var ipString = ip.toString(ipBuff);
  if (!ipString || !ip.isIP(ipString)) {
    throw new Error('invalid ip address');
  }
  return ipString;
}

/**
 * @param {number} port
 */
function port2bytes(port) {
  var buf = new ArrayBuffer(2);
  var view = new DataView(buf);
  view.setUint16(0, port);
  return new Uint8Array(buf);
}

/**
 * @param {Uint8Array} buf
 */
function bytes2port(buf) {
  var view = new DataView(buf.buffer);
  return view.getUint16(buf.byteOffset);
}

/**
 * @param {string} str
 */
function str2bytes(str) {
  var buf = uint8ArrayFromString(str);
  var size = Uint8Array.from(varint.encode(buf.length));
  return uint8ArrayConcat([size, buf], size.length + buf.length);
}

/**
 * @param {Uint8Array} buf
 */
function bytes2str(buf) {
  var size = varint.decode(buf);
  buf = buf.slice(varint.decode.bytes);
  if (buf.length !== size) {
    throw new Error('inconsistent lengths');
  }
  return uint8ArrayToString(buf);
}

/**
 * @param {string | Uint8Array | CID} hash
 */
function mh2bytes(hash) {
  // the address is a varint prefixed multihash string representation
  var mh = new CID(hash).multihash;
  var size = Uint8Array.from(varint.encode(mh.length));
  return uint8ArrayConcat([size, mh], size.length + mh.length);
}

/**
 * Converts bytes to bas58btc string
 *
 * @param {Uint8Array} buf
 * @returns {string} bas58btc string
 */
function bytes2mh(buf) {
  var size = varint.decode(buf);
  var address = buf.slice(varint.decode.bytes);
  if (address.length !== size) {
    throw new Error('inconsistent lengths');
  }
  return uint8ArrayToString(address, 'base58btc');
}

/**
 * @param {string} str
 */
function onion2bytes(str) {
  var addr = str.split(':');
  if (addr.length !== 2) {
    throw new Error('failed to parse onion addr: ' + addr + ' does not contain a port number');
  }
  if (addr[0].length !== 16) {
    throw new Error('failed to parse onion addr: ' + addr[0] + ' not a Tor onion address.');
  }

  // onion addresses do not include the multibase prefix, add it before decoding
  var buf = multibase.decode('b' + addr[0]);

  // onion port number
  var port = parseInt(addr[1], 10);
  if (port < 1 || port > 65536) {
    throw new Error('Port number is not in range(1, 65536)');
  }
  var portBuf = port2bytes(port);
  return uint8ArrayConcat([buf, portBuf], buf.length + portBuf.length);
}

/**
 * @param {string} str
 */
function onion32bytes(str) {
  var addr = str.split(':');
  if (addr.length !== 2) {
    throw new Error('failed to parse onion addr: ' + addr + ' does not contain a port number');
  }
  if (addr[0].length !== 56) {
    throw new Error('failed to parse onion addr: ' + addr[0] + ' not a Tor onion3 address.');
  }
  // onion addresses do not include the multibase prefix, add it before decoding
  var buf = multibase.decode('b' + addr[0]);

  // onion port number
  var port = parseInt(addr[1], 10);
  if (port < 1 || port > 65536) {
    throw new Error('Port number is not in range(1, 65536)');
  }
  var portBuf = port2bytes(port);
  return uint8ArrayConcat([buf, portBuf], buf.length + portBuf.length);
}

/**
 * @param {Uint8Array} buf
 */
function bytes2onion(buf) {
  var addrBytes = buf.slice(0, buf.length - 2);
  var portBytes = buf.slice(buf.length - 2);
  var addr = uint8ArrayToString(addrBytes, 'base32');
  var port = bytes2port(portBytes);
  return addr + ':' + port;
}
